import React, { useState } from 'react'

const App = () => {

    const [height,setHeight]=useState('')
    const [weight,setWeight]=useState('')
    const [result,setResult]=useState('')

    const calculate=()=>{
        if(!height||!weight){
            alert("Please enter your height and weigth");
        }
        else{
            let bmi=(weight/((height/100)*(height/100))).toFixed(2);
            setResult(bmi);
        }
    }


    return (
        <>
        <div className="container">
            <h1>BMI CALCULATOR</h1>
            <div className="height">
            <p className="h">Height</p>
            <input type="text" placeholder="Enter your Heigth"  value={height} onChange={(e)=>setHeight(e.target.value)}/>
            </div>

            <div className="weight">
            <p className="w">Weigth</p>
            <input type="text" placeholder="Enter your Weight" value={weight} onChange={(e)=>setWeight(e.target.vlue)}/>
            </div>
            <br />
            <button onClick={calculate}>Check</button>
            <p className="result">{result}</p>
        </div>
        </>
    )
}

export default App
