import React from 'react'
import { useEffect, useState } from 'react/cjs/react.development'

function App() {
    
    const [city,setCity]=useState('')
    const [search,setSearch]=useState('')
    const date= new Date();
    const year=date.getFullYear();
    const month=date.getMonth();
    const day=date.getDate();
    const hours=date.getHours();
    const min=date.getMinutes();

    useEffect(()=>{
        const fetchApi=async()=>{
            const url=`https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=03b2d3015ebcba59c0eea874c85ca560`;
            const response=await fetch(url);
            //console.log(response);
            const res=await response.json();
            //console.log(res);
            setCity(res.main)
            
        }
        fetchApi();

    },[search])
   


    return (
        <>
        <div className="container">
        <div className="input">
                <input type="search" placeholder="Name a city" value={search} onChange={(e)=>{setSearch(e.target.value)}}/>
            </div>

         {!city ? (<><p className="no-data">Please Enter a City</p>
         <p className="date"> {day}-{month+1}-{year}</p>
         <p className="date"> {hours}:{min}</p>
         </>
         )
         :(  <>
           
            <div className="city">
                <p>{search}</p> 
                </div>
                <div className="temp">
                    <p>{city.temp}°c</p>
                </div>
                <div className="box">
                <div className="sub-temp">
                    <h3>Max  {city.temp_max}°c</h3>
                </div>
                <div className="sub-temp">
                    <h3>Min {city.temp_min}°c</h3>
                </div>
                
                </div>
                <div className="humidity">
                    <h3>Humidity {city.humidity}%</h3>
                </div>
                <div className="time">
                    <p>{day}-{month+1}-{year}</p>
                    <p>{hours}:{min}</p>
                </div>
               
                </>)
         
        }
            

              
               
        </div>
        </>
    )
}

export default App
