import React from 'react'
import { useState } from 'react/cjs/react.development'

function App() {

    const [search,setSearch]=useState('')
    const [weather,setWeather]=useState('')

    const getWeather=()=>{
        const url=`https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=03b2d3015ebcba59c0eea874c85ca560`;
        fetch(url)
        .then(res=>res.json())
        .then(data=>{
            console.log(data);
            setWeather(data.main)
        })
    }

    return (
        <>
      <div className="container">
          <h1>Weather App</h1>
          <h2 id="enter">Enter a city</h2>
          <input type="text" placeholder='City' value={search} onChange={(e)=>setSearch(e.target.value)}/>
          <button onClick={getWeather}>Search</button>
          <h1 id="main">TEMP {weather.temp}°C</h1>
          <div className="temps">
              <p className="temp" id="min">
                  MIN {weather.temp_min}°C
              </p>
              <p className="temp" id="man">
                  MAX {weather.temp_max}°C
              </p>
          </div>
         
          <p classname="humids">Humidity{weather.humidity}</p>
      </div>
        </>
    )
}

export default App
