import React, { useState } from 'react'

function App() {
    const [input,setInput]=useState('');
    
    const [comp,setComp]=useState('')
    const [result,setResult]=useState('')

const check=()=>{
    var num=Math.floor(Math.random()*5);
    setComp(num);
    let final=res(num);
    setResult(final);
}
const res=(num)=>{
    if(num===input){
        return "You Guessed it right"
    }
    else{
        return "You Guessed it wrong"
    }
}


    return (
        
           <>
        <div className="container">
            <h1>Number Guessing Game</h1>
            <h3>Enter a number less than 5</h3>
            <input type="Number" placeholder="Number" value={input} onChange={(e)=>setInput(e.target.value)} />
            <br />
            <button onClick={check}>Check</button>
            <p>You guessed : {input}</p>
            <p>Computer chose : {comp}</p>
            <p>{result}</p>
        </div>
        </>
        
    )
}

export default App
